if (typeof msg.info != 'string') { msg.info = new Date().toLocaleString(); }
if (typeof msg.solar != 'string') { msg.solar = "-" }
if (typeof msg.house != 'string') { msg.house = "-" }
if (typeof msg.fromGrid != 'string') { msg.fromGrid = "-" }
if (typeof msg.toGrid != 'string') { msg.toGrid = "-" }
return msg;

if (Number.isFinite(msg.info)) { msg.info = msg.info.toString() }
if (Number.isFinite(msg.solar)) { msg.solar = msg.solar.toString() }
if (Number.isFinite(msg.house)) { msg.house = msg.house.toString() }
if (Number.isFinite(msg.fromGrid)) { msg.fromGrid = msg.fromGrid.toString() }
if (Number.isFinite(msg.toGrid)) { msg.toGrid = msg.toGrid.toString() }
return msg;

var unit = env.get("Unit");

msg.solar = msg.solar + unit;
msg.house = msg.house + unit;
msg.fromGrid = msg.fromGrid + unit;
msg.toGrid = msg.toGrid + unit;
return msg;

if (env.get("Calculate house") && Number.isFinite(msg.solar) && Number.isFinite(msg.fromGrid))
{
    msg.house = (msg.solar + msg.fromGrid);
    if (Number.isFinite(msg.toGrid)) 
    {
        msg.house = msg.house - msg.toGrid;
    }
}
return msg;

var roundMode = env.get("Round mode");
var decimals = env.get("Decimals");
if (roundMode == "Round")
{
    if (Number.isFinite(msg.solar)) { msg.solar = round(msg.solar, decimals); }
    if (Number.isFinite(msg.house)) { msg.house = round(msg.house, decimals); }
    if (Number.isFinite(msg.fromGrid)) { msg.fromGrid = round(msg.fromGrid, decimals); }
    if (Number.isFinite(msg.toGrid)) { msg.toGrid = round(msg.toGrid, decimals); }
}
if (roundMode == "To fixed") {
    if (Number.isFinite(msg.solar)) { msg.solar = msg.solar.toFixed(decimals); }
    if (Number.isFinite(msg.house)) { msg.house = msg.house.toFixed(decimals); }
    if (Number.isFinite(msg.fromGrid)) { msg.fromGrid = msg.fromGrid.toFixed(decimals); }
    if (Number.isFinite(msg.toGrid)) { msg.toGrid = msg.toGrid.toFixed(decimals); }
}

return msg;

function round(num, decimals) {
    return +(Math.round(num + "e+" + decimals) + "e-" + decimals);
}

