function unitConvert(num, inputUnit, outputUnit) {
    if (inputUnit == outputUnit){return num;}

    if(inputUnit == "W")
    {
        if (outputUnit == "kW") {return powerOfTen(num, -3);}
        else {node.error("can't convert " + inputUnit + " to " + outputUnit);}
    }

    if (inputUnit == "kW") 
    {
        if (outputUnit == "W") {return powerOfTen(num, 3);}
        else {node.error("can't convert " + inputUnit + " to " + outputUnit);}
    }

    if (inputUnit == "Wh")
    {
        if (outputUnit == "kWh") {return powerOfTen(num, -3);}
        else if (outputUnit == "MWh") {return powerOfTen(num, -6);}
        else {node.error("can't convert " + inputUnit + " to " + outputUnit);}
    }

    if (inputUnit == "kWh") {
        if (outputUnit == "Wh") {return powerOfTen(num, 3);}
