function unitConvert(num, inputUnit, outputUnit) {
    if (inputUnit == outputUnit) { return num; }

    else if (inputUnit == "0-1%") {
        if (outputUnit == "%") { return num * 100; }
        else { node.error("can't convert " + inputUnit + " to " + outputUnit); }
    }

    else if (inputUnit == "W") {
        if (outputUnit == "kW") { return powerOfTen(num, -3); }
        else { node.error("can't convert " + inputUnit + " to " + outputUnit); }
    }
    else if (inputUnit == "kW") {
        if (outputUnit == "W") { return powerOfTen(num, 3); }
        else { node.error("can't convert " + inputUnit + " to " + outputUnit); }
    }

    else if (inputUnit == "Wmin") {
        num /= 60;
        if (outputUnit == "Wh") { num; }
        else if (outputUnit == "kWh") { return powerOfTen(num, -3); }
        else if (outputUnit == "MWh") { return powerOfTen(num, -6); }
        else { node.error("can't convert " + inputUnit + " to " + outputUnit); }
    }
    else if (inputUnit == "Wh") {
        if (outputUnit == "kWh") { return powerOfTen(num, -3); }
        else if (outputUnit == "MWh") { return powerOfTen(num, -6); }
        else { node.error("can't convert " + inputUnit + " to " + outputUnit); }
    }
    else if (inputUnit == "kWh") {
        if (outputUnit == "Wh") { return powerOfTen(num, 3); }
        else if (outputUnit == "MWh") { return powerOfTen(num, -3); }
        else { node.error("can't convert " + inputUnit + " to " + outputUnit); }
    }
    else if (inputUnit == "MWh") {
        if (outputUnit == "Wh") { return powerOfTen(num, 6); }
        else if (outputUnit == "kWh") { return powerOfTen(num, 3); }
        else { node.error("can't convert " + inputUnit + " to " + outputUnit); }
    }

    else if (inputUnit == "€") {
        if (outputUnit == "T€") { return powerOfTen(num, -3); }
        else { node.error("can't convert " + inputUnit + " to " + outputUnit); }
    }
    else if (inputUnit == "T€") {
        if (outputUnit == "€") { return powerOfTen(num, 3); }
        else { node.error("can't convert " + inputUnit + " to " + outputUnit); }
    }

    else if (inputUnit == "°C") {
        if (outputUnit == "°F") { return num * 1.8 + 32 }
        else if (outputUnit == "K") { return num + 273.15 }
        else { node.error("can't convert " + inputUnit + " to " + outputUnit); }
    }
    else if (inputUnit == "°F") {
        if (outputUnit == "°C") { return (num - 32) * 5/9 }
        else if (outputUnit == "K") { return (num - 32) * 5/9 + 273.15}
        else { node.error("can't convert " + inputUnit + " to " + outputUnit); }
    }
    else if (inputUnit == "K") {
        if (outputUnit == "°C") { return num - 273.15 }
        else if (outputUnit == "°F") { return (num - 32) * 5/9 }
        else { node.error("can't convert " + inputUnit + " to " + outputUnit); }
    }
}

